    private void jButton9MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton9MouseClicked

        float total_money,spend_money,left_money,num;
        if(index == 0)
        {
             total_money = Float.parseFloat(jTextField1.getText());
             spend_money = Float.parseFloat(jTextField2.getText());
             left_money = total_money - spend_money;
              jTextField4.setText(String.valueOf(spend_money));
              jTextField3.setText(String.valueOf(left_money));
              index = 1;
        }
        else
        {
             total_money = Float.parseFloat(jTextField3.getText());
             spend_money = Float.parseFloat(jTextField2.getText());
             num = Float.parseFloat(jTextField4.getText());
             left_money = total_money - spend_money;
             jTextField4.setText(String.valueOf(spend_money+num));
             jTextField3.setText(String.valueOf(left_money));
           
        }
        
        
       
//        df.num1 = Float.parseFloat(jTextField1.getText());
//        df.num2 = Float.parseFloat(jTextField2.getText());
//        df.num7= df.num1-df.num2;
         jDialog3.hide();   
         jDialog4.show();
    }//GEN-LAST:event_jButton9MouseClicked

