    private void jButton10MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton10MouseClicked
        float num,num1,num2,num3,num4,num5,result;
        num = Float.parseFloat(jTextField1.getText());
        num1 = Float.parseFloat(jTextField4.getText());
        num2 = Float.parseFloat(jTextField5.getText());
        num3 = Float.parseFloat(jTextField6.getText());
        num4 = Float.parseFloat(jTextField7.getText());
        num5 = Float.parseFloat(jTextField8.getText());
        result = num - num1 - num2 - num3 - num4 - num5;
        jTextField3.setText(String.valueOf(result));
        jDialog1.hide();
        jDialog4.show();     
    }//GEN-LAST:event_jButton10MouseClicked

