    private void jButton17MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton17MouseClicked
         float total_money,spend_money,left_money,num;
         if(index == 0)
         {
             total_money = Float.parseFloat(jTextField1.getText());
             spend_money = Float.parseFloat(jTextField12.getText());
             left_money = total_money - spend_money;
             jTextField8.setText(String.valueOf(spend_money));
             jTextField3.setText(String.valueOf(left_money));
             index = 1;
         }
         else
         {
             total_money = Float.parseFloat(jTextField3.getText());
             spend_money = Float.parseFloat(jTextField12.getText());
             num = Float.parseFloat(jTextField8.getText());
             left_money = total_money - spend_money;
             jTextField8.setText(String.valueOf(spend_money+num));
             jTextField3.setText(String.valueOf(left_money));
         }
         
         jDialog8.hide();   
         jDialog4.show();
    }//GEN-LAST:event_jButton17MouseClicked

